import { useState } from "react";
import GameBoard from "./GameBoard";
import "./App.css";

const Title = () => {
  const [visiblity, setVisibility] = useState(false);
  if (visiblity) {
    return (
      <div>
        <div className="Title" style={{display: "none"}}></div>
        <GameBoard/>
      </div>
    );
  }
  return (
    <div className="Title">
      <div className="Rules">
        <h3>Rules of Go Fish!</h3>
        <hr />
        <ul>
          <li>Each player begins the game with five cards in their hand.</li>
          <li>You match two cards of the same value to get a "book."</li>
          <li>
            If the player does not have the card, you draw one card and pass.
          </li>
          <li>The player with the most books wins!</li>
        </ul>
      </div>
      <div className="Credits">
        <p>
          Creators:{" "}
          <a
            href="https://github.com/python-42/"
            target="_blank"
            rel="noreferrer"
          >
            Jake H.
          </a>{" "}
          and{" "}
          <a
            href="https://github.com/jagernet-ops"
            target="_blank"
            rel="noreferrer"
          >
            Michael S.
          </a>
        </p>
      </div>
      <button
        id="startButton"
        type="button"
        onClick={() => {
          setVisibility(true);
          startGame = true;
        }}
      >
        <h2 style={{ textDecorationLine: "underline" }}>
          üêü Start Fast Fish! üé£
        </h2>
      </button>
    </div>
  );
};

export {Title, };
